var mongoose=require("./common").mongoose,assert=require("assert"),Schema=mongoose.Schema;describe("schematype",function(){it("honors the selected option",function(){var c=new Schema({thought:{type:String,select:false}});assert.equal(false,c.path("thought").selected);var b=new Schema({thought:{type:String,select:true}});assert.equal(true,b.path("thought").selected)});it("properly handles specifying index in combination with unique or sparse",function(){var a=new Schema({name:{type:String,index:true,unique:true}});assert.deepEqual(a.path("name")._index,{unique:true});var a=new Schema({name:{type:String,unique:true,index:true}});assert.deepEqual(a.path("name")._index,{unique:true});var a=new Schema({name:{type:String,index:true,sparse:true}});assert.deepEqual(a.path("name")._index,{sparse:true});var a=new Schema({name:{type:String,sparse:true,index:true}});assert.deepEqual(a.path("name")._index,{sparse:true})})});